var pages = pages || {};
pages.usuario = pages.usuario || {};

pages.usuario.model = function () {

    var vmUsuario = function (usuario) {
        var self = this;

        self.usuarioId = ko.observable();
        self.nome = ko.observable();
        self.email = ko.observable();
        self.senha = ko.observable();
        self.confirmarSenha = ko.observable();
        self.telefone = ko.observable();
        self.celular = ko.observable();
        self.perfil = ko.observable();      
        self.endereco = ko.observable(new vmEndereco()); 
        self.dataCadastro = ko.observable();  

        if (usuario) {
            self.usuarioId(usuario.id);
            self.nome(usuario.nome);
            self.email(usuario.email);            
            self.telefone(usuario.telefone);
            self.celular(usuario.celular);
            self.perfil(usuario.role);          
            self.endereco(new vmEndereco(usuario.endereco));            
            self.dataCadastro(usuario.dataCadastro);
        }
    };

    var vmEndereco = function (endereco) {
        var self = this;

        self.logradouro = ko.observable();
        self.numero = ko.observable();
        self.cep = ko.observable();
        self.bairro = ko.observable();
        self.complemento = ko.observable();
        self.municipioId = ko.observable();
        self.municipioNome = ko.observable();
        self.estadoId = ko.observable();
        self.estadoNome = ko.observable();

        if (endereco) {
            self.logradouro(endereco.logradouro);
            self.numero(endereco.numero);
            self.cep(endereco.cep);
            self.bairro(endereco.bairro);
            self.complemento(endereco.complemento);
            self.municipioId(endereco.municipioId);
            self.municipioNome(endereco.municipioNome);
            self.estadoId(endereco.estadoId);
            self.estadoNome(endereco.estadoNome);
        }

    };  

    var vmEstado = function (estado) {
        var self = this;

        self.estadoId = ko.observable(estado.estadoId);
        self.nome = ko.observable(estado.nome);
        self.sigla = ko.observable(estado.sigla);
    };

    var vmMunicipio = function (municipio) {
        var self = this;

        self.municipioId = ko.observable(municipio.municipioId);
        self.nome = ko.observable(municipio.nome);
    }; 

    return {
        vmUsuario,
        vmEstado,
        vmMunicipio       
    };

}();
var pages = pages || {};
pages.usuario = pages.usuario || {};

pages.usuario.services = function () {  

    var EPerfil = {
        ADMINISTRADOR: "Administrador",
        GESTOR: "Gestor",
        USUARIO: "Usuario",
        FUNCIONARIO: "Funcionário"
    };    

    var obterTodos = function () {
        var url = pages.metadata.actionUrl("/api/gateway/usuarios");
        return pages.dataServices.get(url);
    }

    var obterPorId = function (id) {
        var url = pages.metadata.actionUrl("/api/gateway/usuarios/" + id);
        return pages.dataServices.get(url);
    }

    var salvar = function (parametro) {
        var url = pages.metadata.actionUrl("/api/gateway/usuarios");
        return pages.dataServices.postAjax(url, parametro);
    }    

    var atualizar = function (id, parametro) {
        var url = pages.metadata.actionUrl("/api/gateway/usuarios/" + id);
        return pages.dataServices.putAjax(url, parametro);
    }   

    var deletar = function (id) {
        var url = pages.metadata.actionUrl("/api/gateway/usuarios/" + id);
        return pages.dataServices.deleteAjax(url);
    }  

    var obterMunicipioPorEstadoId = function (estadoId) {
        var url = pages.metadata.actionUrl("/api/gateway/municipios/estado/" + estadoId);
        return pages.dataServices.get(url);
    }

    var obterEstados = function () {
        var url = pages.metadata.actionUrl("/api/gateway/estados");
        return pages.dataServices.get(url);
    }

    return {    
        EPerfil,
        obterEstados,
        obterMunicipioPorEstadoId,       
        obterTodos,
        obterPorId,
        atualizar,
        salvar,
        deletar
    };
}();
var pages = pages || {};
pages.usuario = pages.usuario || {};
pages.usuario.model = pages.usuario.model || {};
pages.usuario.services = pages.usuario.services || {};

pages.metadata = pages.metadata || {};
pages.dataServices = pages.dataServices || {};
pages.utils = pages.utils || {};

pages.usuario.viewModel = function () {   
    var model = pages.usuario.model;
    var service = pages.usuario.services;
   
    var viewModelUsuario = new function () {
        var self = this;       
        
        self.usuarios = ko.observableArray([]);
        self.datatable = ko.observable();        

        self.init = function () {            
            self.obterUsuarios();            
        };
        
        self.obterUsuarios = function () {
            pages.dataServices.bloquearTela();
            service.obterTodos().then(function (result) {
                result.data.forEach(function (item) {
                    self.usuarios.push(new model.vmUsuario(item));
                });                
            }).catch(function (result) {
                console.log(result.data);
            }).finally(function () {
                self.inicializarDatatable();
                pages.dataServices.desbloquearTela();
            });
        };
      
        self.inicializarDatatable = function () {
            var table = $('#datatable-usuario').DataTable({
                lengthChange: false,
                order: [[1, 'asc']],
                responsive: true,
                columnDefs: [
                {
                    targets: [0],
                    orderable: false,
                    searchable: false,
                    visible: false
                },
                {
                    targets: [5],
                    orderable: false,
                    searchable: false,
                    className: "text-center"
                }],
                buttons: [
                    {
                        extend: 'pdfHtml5',
                        title: 'Listagem Usuários'
                    },
                ],
                language: pages.utils.languageDataTablePtBr               
            });

            table.buttons().container().appendTo('#datatable-usuario_wrapper .col-md-6:eq(0)');            
            self.datatable(table);
        }; 

        self.editar = function (usuarioId) {
            pages.dataServices.bloquearTela()
            window.location.href = "/Usuario/Edicao/" + usuarioId;
        };

        self.visualizar = function (usuarioId) {
            pages.dataServices.bloquearTela();
            window.location.href = "/Usuario/Visualizar/" + usuarioId;
        };

        self.excluir = function (usuarioId) {
            bootbox.dialog({
                closeButton: false,
                message: "Confirma a exclusão do usuário?",               
                buttons: {
                    nao: {
                        label: "NÃO",
                        className: "btn-sm btn-danger"
                    },
                    sim: {
                        label: "SIM",
                        className: "btn-sm btn-primary",
                        callback: function () {
                            pages.dataServices.bloquearTela();
                            service.deletar(usuarioId).then(function () {
                                bootbox.alert("Usuário excluído com sucesso!", function () {  
                                    location.reload();                                                                        
                                });                                 
                            }).catch(function (result) {
                                if (result.exibeMensagem)
                                    bootbox.alert(result.data);
                            }).finally(function () {
                                pages.dataServices.desbloquearTela();
                            });                            
                        }
                    }                    
                }
            });            
        };       

        self.init();

    };

    ko.applyBindings(viewModelUsuario, bindingBody);

    $('#datatable-usuario tbody').on('click', '#btnVisualizar', function (event) {
        var usuarioId = event.currentTarget.value;
        viewModelUsuario.visualizar(usuarioId);
    });

    $('#datatable-usuario tbody').on('click', '#btnEditar', function (event) {
        var usuarioId = event.currentTarget.value;
        viewModelUsuario.editar(usuarioId);
    });   

    $('#datatable-usuario tbody').on('click', '#btnExcluir', function (event) {
        var usuarioId = event.currentTarget.value;
        viewModelUsuario.excluir(usuarioId);
    });    
    
}();